import { useCallback, useEffect, useState } from 'react';
import Card from '../UI/Card';
import classes from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';


const AvailableMeals = () => {
  const [mealsData, setMealsData] = useState({
    id: '',
    name: '',
    description: '',
    price: null
  });

  const fetchMealsHandler = useCallback(async () => {
    const response = await fetch('https://react-http-90a8e-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json');

    const data = await response.json();

    for (const key in data) {
      mealsData.push({
        id: key,
        name: data[key].name,
        description: data[key].description,
        price: data[key].price
      });
    }

    console.log(mealsData);
  }, []);

  useEffect(() => {
    fetchMealsHandler();
  }, [fetchMealsHandler]);

  const mealsList = mealsData.map(meal => {
      return(
        <>
          <MealItem id={meal.id} key={meal.id} name={meal.name} description={meal.description} price={meal.price} />
        </>
      )
  });

  return (
      <section className={classes.meals}>
          <Card>  
            <ul>
                {mealsList}
            </ul>
          </ Card>
      </section>
    )
};

export default AvailableMeals;
